import React from 'react'
import './Preview.css'

class Preview extends React.Component {
    render () {
        return (
            <React.Fragment>
            {!this.props.previewLoading
                ? 
                <div className="preview">
                    <div className="previewImage">
                        <img src={this.props.og.icon} alt="url" />
                    </div>
                    <div className="previewTitle">
                        {this.props.og.title}
                    </div>
                    <div className="previewDes">
                        {this.props.og.description}
                    </div> 
                </div>
                :
                <div className="previewLoading">Loading...</div>
            }
            </React.Fragment>
        )
    }
}

export default Preview
