import React from 'react'
import './AddModal.css'
import url from './url-white-x.png'
import axios from 'axios'
import Preview from '../Preview/Preview';

class AddModal extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            url: '',
            apiResponse: '',
            previewLoading: false,
            gotResponse: false
        }
    }

    onChange = event => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    postUrl = () => {
        this.setState({
            previewLoading: true
        })
        var url = {
            urlgot: this.state.url
        }
        axios
          .post('http://localhost:9000/url', url)
            .then(res => {this.setState({apiResponse: res.data.other, gotResponse: true})})
            .catch(err => { console.error(err) })
        console.log(this.state.apiResponse)
    }

    saveUrl = () => {

    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClick, false);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClick, false);
    }

    handleClick = (e) => {
        if (!this.node.contains(e.target)) {
            return (this.props.changeModalVisibility(false));
        }
    }

    render () {
        return (
            <div id="pageMask" >
                <div className="addUrlModal" role="dialog" ref={node => this.node = node}>
                    <div className="addUrlInputGroup">
                        <div className="urlIcon">
                            <img src={url} alt="url icon" />
                        </div>
                        <input autoFocus onChange={this.onChange} required value={this.state.url} className="addUrlInput" type="text" name="url" placeholder="add url here..." />
                        {this.state.gotResponse
                            ?
                            <button onClick={this.saveUrl} className="modalButton">Save</button>
                            :
                            <button onClick={this.postUrl} className="modalButton">Load</button>
                        }
                    </div>
                    {this.state.previewLoading && <Preview og={this.state.apiResponse} gotResponse={this.gotResponse} />}
                </div>
            </div>
        );
    }


}

export default AddModal
