import React from 'react'
import './AddModal.css'

class AddModal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            url: "",
        };
    }

    render () {
        return (
            <div id="page-mask" >
                <form className="add-modal" role="dialog" ref={node => this.node = node}>
                    <input autoFocus onChange={this.editUrl} required className="add-input" type="text" name="url" placeholder="add url here..."></input> 
                </form>
            </div>
        );
    }

    editUrl = event => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    componentWillMount() {
        document.addEventListener('mousedown', this.handleClick, false);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClick, false);
    }

    handleClick = (e) => {
        if (!this.node.contains(e.target)) {
            return (this.props.checkIfAddingTasks(false));
        }
    }

}

export default AddModal;
