import React from 'react'
import './AddModal.css'

class AddModal extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            url: ''
        }
    }

    onChange = event => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    render () {
        return (
            <div id="page-mask" >
                <form className="add-modal" role="dialog" ref={node => this.node = node}>
                    <input autoFocus onChange={this.handleChange} required value={this.state.url} className="url-input" type="text" name="url" placeholder="add url here..."></input>
                    <div className="">

                    </div>
                </form>
            </div>
        );
    }

    // editUrl = event => {
